function generate(number) {
  let g = [];
  
let string = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";
     
  string = string.split("");
  for(let k = 0; k < number; k++) {
    g.push(string[Math.floor(Math.random() * string.length)]);
  }
  return g.join("");
}

module.exports = {generate}

